var LimitCount = 2;
//Inicio Script

function run(IDblock, OldReturn) {
  if(OldReturn === 'primeira entrada'){

     var retorno = new Object()
    retorno.IDblock = IDblock
    retorno.count = 1
    retorno.response = false
    retorno.detail = 'Atendimento novo, criando variavel'

    return retorno

  }
  try {
    IDblock = JSON.parse(IDblock).lastStateWithInput;
    OldReturn = JSON.parse(OldReturn);

    lastBlock = OldReturn.IDblock
    count = OldReturn.count

    if(lastBlock != IDblock){

      var retorno = new Object()
      retorno.IDblock = IDblock
      retorno.count = 1
      retorno.response = false
      retorno.detail = 'Bloco novo'

      return retorno

    }else{

      count = count + 1
      var retorno = new Object()
      retorno.IDblock = IDblock


      if(count >= LimitCount){

        retorno.count = count
        retorno.response = true
        retorno.detail = 'limite atingido no bloco: '+IDblock

        return retorno

      }else{

        retorno.count = count
        retorno.response = false
        retorno.detail = 'Aumentou contagem de erros do bloco : ' + IDblock + 'para :' + count + 'o limite atual e: ' + LimitCount

        return retorno

      }

    }

  } catch (error) {

    var retorno = new Object()
    retorno.IDblock = IDblock
    retorno.count = 1
    retorno.response = false
    retorno.detail = 'Atendimento novo, criando variavel'

    return retorno

  }

}

